CREATE TABLE IF NOT EXISTS `sharing_writing` (
  `id` int(14) NOT NULL AUTO_INCREMENT,
  `name` varchar(300) COLLATE utf8_unicode_ci NOT NULL,
  `content` text COLLATE utf8_unicode_ci NOT NULL,
