--
-- Dumping data for table `sharing_writing`
--

INSERT INTO `sharing_writing` (`id`, `name`, `content`, `type`) VALUES
(1, 'Unit 2: Format của phần I – Bài thi TOEIC Writing\r\n', 'Sau đây là dạng format (hình thức) thi Part I của bài thi TOEIC Writing:\r\n\r\nQuestion 1 - 5: Write a sentence based on a picture - TOEIC Writing\r\n\r\nDirections: In this part of the test, you will write ONE sentence that is based on a picture. With each picture you will be given TWO words or phrases that you must use your sentence. You can change the forms of the words and you can use the words in any order.\r\n\r\nYour sentence will be scored on\r\n\r\n·         The appropriate use of grammar, and\r\n·         The relevance of the sentence to the picture\r\nIn this part, you can move to the next question by clicking on Next. If you want to return to a previous question, click on Back.\r\n\r\nYou will have eight minutes to complete this part of the test.', 1),
(2, 'Unit 3: Format của Phần II – TOEIC Writing Test\r\n', 'Sau đây là dạng format (hình thức) thi Part II của bài thi TOEIC Writing:\r\n\r\nQuestion 6-7: Respond to the written request - TOEIC Writing\r\n\r\nDirections: In this part of the test, you will show how well you can write a response to an email.\r\nYour response will be scored on\r\n\r\n·         The quality and variety of your sentences\r\n·         Vocabulary, and\r\n·         Organization\r\n\r\nYou will have 10 minutes to answer each email.', 1),
(3, 'Unit 4: Format của Phần III – TOEIC Writing Test\r\n', 'Sau đây là dạng format (hình thức) thi Part III của bài thi TOEIC Writing:\r\n\r\n Question: Write an opinion essay - TOEIC Writing\r\n\r\nDirection: In this part of the test, you will write an essay in response to a question that asks you to state, explain, and support your opinion on an issue. Typically, an effective essay will contain a minimum of 300 words.\r\n\r\nYour response will be scored on\r\nWhether your opinion is supported with reasons and/or examples,\r\nGrammar\r\nVocabulary, and\r\nOrganization.\r\nYou will have 30 minutes to plan, write, and revise your essay.', 1),
(4, 'Unit 1: Thông tin chung về kĩ năng thi TOEIC Writing Test\r\n', ' \r\n1. Đặc trưng bài thi TOEIC Writing\r\n\r\nBài kiểm tra Writing Toeic Test nhằm kiểm tra “năng lực viết” (Writing competency) của người sử dụng Tiếng Anh để truyền đạt thông tin hiệu quả trong môi trường kinh doanh quốc tế. Bài thi TOEIC Writing Test là cơ sở giúp các nhà quản lý cũng như những người làm công tác nhân sự đưa ra quyết định phù hợp trong việc sắp xếp các vị trí hoặc đề bạt nhân sự.\r\n\r\n2. Cấu trúc bài thi TOEIC Writing\r\n\r\nBài thi TOEIC Writing Test được chia làm 3 phần (part) bao gồm 8 câu hỏi. Câu hỏi ở phần đầu nói chung là dễ, càng về sau mức độ khó càng tăng dần. Tổng thời lượng cho bài thi TOEIC Writing Test là khoảng 60 phút.', 1);

